import React, { useState, useEffect, useMemo } from 'react';
import ControleLivros from './controle/ControleLivros';
import ControleEditora from './controle/ControleEditora';

const LivroLista = () => {
  const [livros, setLivros] = useState([]);

  // Memoize the controleLivro and controleEditora instances
  const controleLivro = useMemo(() => new ControleLivros(), []);
  const controleEditora = useMemo(() => new ControleEditora(), []);

  useEffect(() => {
    const carregarLivros = async () => {
      const livros = await controleLivro.obterLivros();
      setLivros(livros);
    };

    carregarLivros();
  }, [controleLivro]);

  return (
    <div className="container mt-4">
      <h2>Lista de Livros</h2>
      <ul className="list-group">
        {livros.map((livro) => (
          <li key={livro.codigo} className="list-group-item">
            <h5>{livro.titulo}</h5>
            <p>{livro.resumo}</p>
            <p><strong>Editora:</strong> {controleEditora.getNomeEditora(livro.codEditora)}</p>
            <p><strong>Autores:</strong> {livro.autores.join(', ')}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default LivroLista;
