import  { useState } from 'react';
import { ControleLivros } from './controle/ControleLivros';
import { ControleEditora } from './controle/ControleEditora';
import './App.css';

const LivroDados = () => {
    const controleLivro = new ControleLivros();
    const controleEditora = new ControleEditora();
    const opcoes = controleEditora.getEditoras().map((editora) => ({
        value: editora.codEditora,
        text: editora.nome
    }));

    const [titulo, setTitulo] = useState('');
    const [resumo, setResumo] = useState('');
    const [autores, setAutores] = useState('');
    const [codEditora, setCodEditora] = useState(opcoes[0].value);

    const tratarCombo = (event) => {
        setCodEditora(Number(event.target.value));
    };

    const incluir = (event) => {
        event.preventDefault();

        const livro = {
            titulo: titulo,
            resumo: resumo,
            autores: autores.split(''),
            codEditora: codEditora
        };

        controleLivro.incluir(livro);
    };

    return (
        <main className="container-dados">
            <h1 className="my-4">Dados do Livro</h1>
            <form onSubmit={incluir}>
                <label>TÃ­tulo:</label>
                <input type="text" className="form-control mb-3" value={titulo} onChange={(e) => setTitulo(e.target.value)} />
                <label>Resumo:</label>
                <textarea className="form-control mb-3" value={resumo} onChange={(e) => setResumo(e.target.value)} />
                <label>Editora:</label>
                <select className="form-select mb-3" value={codEditora} onChange={tratarCombo}>
                                      {opcoes.map((opcao) => (
                                        <option key={opcao.value} value={opcao.value}>{opcao.text}</option>
                                      ))}
                </select>
                <label>Autores (1 por linha):</label>
                <textarea className="form-control mb-3" value={autores} onChange={(e) => setAutores(e.target.value)} />
                <button type="submit" className="btn btn-primary">Salvar Dados</button>
            </form>
        </main>
    );
};

export default LivroDados;
