import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import LivroLista from './LivroLista';
import LivroDados from './LivroDados';
import { ControleLivros } from 'path/to/ControleLivros';
import { ControleEditora } from 'path/to/ControleEditora';
import './App.css';

const App: React.FC = () => {
// Create instances of ControleLivros and ControleEditora
const controleLivros = new ControleLivros();
const controleEditora = new ControleEditora();

return (
<BrowserRouter>
<nav className="navbar navbar-expand-lg navbar-custom bg-dark">
<div className="container">
<Link to="/lista" className="nav-link-custom mx-2">Cat√°logo</Link>
<Link to="/dados" className="nav-link-custom mx-2">Novo</Link>
</div>
</nav>
<div className="container">
<Routes>
<Route path="/lista" element={<LivroLista livros={controleLivros.obterLivros()} editoras={controleEditora.getEditoras()} />} />
<Route path="/dados" element={<LivroDados onSave={(novoLivro) => controleLivros.incluir(novoLivro)} />} />
</Routes>
</div>
</BrowserRouter>
);
};

export default App;
