const App: React.FC = () => {
return (
<BrowserRouter>
<nav className="navbar navbar-expand-lg navbar-dark bg-dark" style={{ borderRadius: '5px', boxShadow: '0 2px 4px rgba(0,0,0,0.2)' }}>
<Link to="/lista" className="nav-link text-white">Cat√°logo</Link>
<Link to="/dados" className="nav-link text-white">Novo</Link>
</nav>
<Routes>
<Route path="/lista" element={<LivroLista />} />
<Route path="/dados" element={<LivroDados />} />
</Routes>
</BrowserRouter>
);
};

export default App;
